package operators;

public class Concatenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "jyothi";
		int age=35;
		double percentage = 78.98;
		System.out.println(name + age + percentage);
		System.out.println(name + "      "+age +"        " +percentage);
		System.out.println(name + " \n"+age +"\n" +percentage);
		System.out.println("my name is :"+name);
		System.out.println("my age is:" + age);
		System.out.println("my percentage is:" + percentage);
		System.out.println("my name is :"+name + "   "+"my age is:" + age+"   "+"my percentage is:" + percentage);
		System.out.println("my name is :"+name + "\n"+"my age is:" + age+"\n"+"my percentage is:" + percentage);
	}

}
